INSERT INTO `news` VALUES (366,153,'第一节  概述',0,NULL,NULL,NULL,'','','','','2011-07-06 20:55:22','<p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><b> </b><p><b><span style=\"color:#e53333;\">一、包装物料的基本物性及分类</span><span style=\"color:#e53333;\">&nbsp;</span></b></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"color:#e53333;\"><span style=\"color:#000000;\">包装物料的</span><span style=\"color:#000000;\">状态、形状和物性</span><span style=\"color:#000000;\">直接影响物料的计量和供送。</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><p><span style=\"color:#000000;\">必须全面了解被包装物料的基本物性，包括：流体的黏度和密</span><span style=\"color:#000000;\">度，粉体的密度和堆积角，颗粒物料的密度和粒度，规则块状</span><span style=\"color:#000000;\">物料的形状（尺寸）和单件重量等。</span></p>\n<span style=\"color:#000000;\"> </span><p><span style=\"color:#000000;\">按其状态和流动性</span><span style=\"color:#000000;\">可分为：流体、半流体（粘稠物料）、散体、</span><span style=\"color:#000000;\">固体；&nbsp;</span></p>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span></div>\n<span style=\"color:#000000;\"> </span></p:colorscheme><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><div v:shape=\"_x0000_s1026\"><div><span style=\"color:#000000;\">按物料形状</span><span style=\"color:#000000;\">可分为：体、颗粒、规则块体、不规则块体等。</span></div>\n</div>\n</p:colorscheme><p><span style=\"color:#e53333;\"><b>&nbsp;&nbsp;<img src=\"/edit/attached/20110706/20110706205818_18757.jpg\" width=\"650\" height=\"150\" alt=\"\" /></b></span></p>\n<p><span style=\"color:#e53333;\"><b><hr />\n<br />\n</b></span></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<p style=\"color:#e53333;font-weight:bold;\"><span style=\"color:#e53333;\">二、包装物料计量供送的基本要求</span></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"color:#e53333;\"><span style=\"color:#000000;\">(1)满足包装工艺要求，实现物料连续性或间歇性供送的要求；</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div style=\"color:#e53333;\"><span style=\"color:#000000;\">(2)根据物料类别和包装要求，实现物料计量及供送的机械化和自</span><span style=\"color:#000000;\">&nbsp;</span><span class=\"Apple-style-span\" style=\"color:#000000;\">动化；</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div style=\"color:#e53333;\"><span style=\"color:#000000;\">(3)满足包装机生产率对物料供送速度和计量精度的要求，生产能</span><span class=\"Apple-style-span\" style=\"color:#000000;\">力、工作速度可调整；</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div style=\"color:#e53333;\"><span style=\"color:#000000;\">(4)包装物料供送的定量应准、定向应好、供送过程要稳、可控性</span><span style=\"color:#000000;\">&nbsp;</span><span style=\"color:#000000;\">要强，便于与包装材料或容器供送机构、包装执行机构同步工</span><span style=\"color:#000000;\">&nbsp;</span><span style=\"color:#000000;\">作及协调布置。</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div style=\"color:#e53333;\"><span style=\"color:#000000;\">(5)便于检测、控制、维修和调试。</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><p style=\"color:#e53333;\"><span style=\"color:#000000;\">(6)满足卫生性和安全性要求，物料损耗小，便于清洗和维护。</span></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<p style=\"color:#ffe500;\"><b><span style=\"color:#e53333;\"><br />\n</span></b></p>\n<p style=\"color:#ffe500;\"><b><span style=\"color:#e53333;\"><hr />\n三、包装物料计量供送的基本形式</span></b><span style=\"color:#e53333;\">&nbsp; &nbsp; </span><span style=\"mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;font-size:24pt;color:#173D89;mso-color-index:3;\"><b><span style=\"mso-spacerun:yes;\">&nbsp;</span></b></span></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<p><span style=\"font-size:12px;color:#000000;\">根据物料的计量方法</span><span style=\"font-size:12px;color:#000000;\">可分为：计数计量法，容</span><span style=\"font-size:12px;color:#000000;\">积计量法，称重计量法；</span></p>\n<span style=\"color:#000000;\"> </span><p><span style=\"font-size:12px;color:#000000;\">根据物料形态和形状</span><span style=\"font-size:12px;color:#000000;\">可分为：流体和半流体的</span><span style=\"font-size:12px;color:#000000;\">计量及供送装置；粉体的计量及供送装置、块</span><span style=\"font-size:12px;color:#000000;\">体的计量及供送装置；&nbsp;</span></p>\n<span style=\"color:#000000;\"> </span><span style=\"font-size:12px;color:#000000;\"> </span><div style=\"color:#173d89;font-size:24pt;\"><span style=\"font-size:12px;color:#000000;\">根椐供送计量机构的类型</span><span style=\"font-size:12px;color:#000000;\">可分为：泵供送及计</span><span style=\"font-size:12px;color:#000000;\">量装置、螺杆供送及计量装置、间歇式斗秤、</span><span style=\"font-size:12px;color:#000000;\">连续式电子秤等。</span></div>\n</div>\n<p></p>\n<p></p>\n<div></div>\n</div>\n<p></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\" style=\"color:#e53333;font-weight:bold;\"></div>\n<p></p>\n<p></p>',0,NULL,'999999',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO `news` VALUES (367,153,'第二节 包装物料的计量装置',0,NULL,NULL,NULL,'','','','','2011-07-06 21:00:41','<p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><b> </b><p><b><span style=\"color:#e53333;\">一、计数计量装置</span><span style=\"color:#e53333;\">&nbsp;&nbsp;</span></b></p>\n<p><span style=\"color:#e53333;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></span></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div><span style=\"font-weight:bold;font-family:Tahoma;font-size:178%;color:red;\"><span style=\"mso-special-format:bullet;color:#5FB6F1;mso-color-index:6;position:absolute;left:-3.71%;font-family:Wingdings;\">v</span></span><span style=\"color:#000000;\">计数法采用机械、光学、电磁等检测方式，测定每一规定批次的产品数量</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div><span style=\"color:#000000;\">v广泛应用于规则的条状、块状、片状、颗粒状产品的包装计量</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><p><span style=\"color:#000000;\">v按计数方式的不同，可将计数计量装置分为单件计数装置、多件计数装置、转盘计数装置和履带式计数装置等。</span></p>\n<p></p>\n<hr />\n<div style=\"text-align:center;\"><embed src=\"http://bzjx.nit.net.cn/uploads/movie/3-11.swf\" type=\"application/x-shockwave-flash\" width=\"550\" height=\"400\" quality=\"high\" /></div>\n<div style=\"text-align:center;\"><hr />\n</div>\n<p style=\"color:#ffe500;\"><b><span style=\"color:#e53333;\">1.</span><span style=\"color:#e53333;\">单件计数装置</span></b></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<p style=\"color:#e53333;\"><span style=\"color:#000000;\">单件计数装置在每通过一件产品时便记一次数，并显示已装件数</span></p>\n<p style=\"text-align:center;color:#e53333;\"><span style=\"color:#ffe500;\"><img src=\"/edit/attached/20110706/20110706210220_75721.jpg\" width=\"400\" height=\"200\" alt=\"\" /><br />\n</span></p>\n<p style=\"text-align:center;color:#e53333;\"><span style=\"color:#ffe500;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><b> </b></p:colorscheme></span></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"text-align:center;mso-char-wrap:1;mso-kinsoku-overflow:1;\"><b><span style=\"color:#e53333;\">螺钉形产品单件计数装置</span></b></div>\n<div style=\"text-align:center;\"><b><span style=\"color:#e56600;\">1-刮板式提升给料器；2-供料辊；3-拨料轮；4-定向滑槽；</span></b></div>\n<span style=\"color:#e56600;\"> </span><span style=\"color:#ffe500;\"><b><span style=\"color:#e56600;\"> </span></b></span><p style=\"text-align:center;\"><b><span style=\"color:#e56600;\">5-光电计数器；6-磁性闸门；7-包装容器；8-电机</span></b></p>\n<p style=\"text-align:center;\"></p>\n<hr />\n<div v:shape=\"_x0000_s1026\" class=\"O\"><div><span style=\"background-color:#ff9900;\">光电片剂计数装置</span>利用旋转平盘，将药粒抛向转盘周边。在转盘周边围板缺口处，药粒将被抛出转盘，进入滑道7。药粒由转盘滑入药粒滑道7时，滑道上安装的光电传感器8检测并输出电脉冲信号，输入到具有“预先设定”和“比较”功能的控制器。当输入的脉冲个数等于预选数目时，控制电磁铁动作，将通道上的翻板翻转，药粒充填到容器中。这种装置可根据要求的瓶装数量值，任意设定控制器参数，不需更换机器零件，即可完成不同计数量的调整。</div>\n</div>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n<div><table style=\"width:100%;\" cellpadding=\"2\" cellspacing=\"0\" border=\"1\" bordercolor=\"#000000\"><tbody><tr><td><p>&nbsp;<b>光电片剂计数装置</b></p>\n<p>1-控制器；2-围板；3-旋转平盘；</p>\n<p>4-回形拨杆；5-输送带；6-容器；</p>\n<p>7-药粒滑道 &nbsp;8-光电传感器；</p>\n<p>9-下料滑板；10-料斗</p>\n</td>\n<td><span class=\"Apple-style-span\" style=\"border-collapse:separate;\"><img src=\"/edit/attached/20110706/20110706210642_15010.jpg\" width=\"250\" height=\"150\" alt=\"\" /></span>&nbsp;</td>\n</tr>\n</tbody>\n</table>\n<br />\n</div>\n<div><hr />\n</div>\n<div><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><p><span style=\"color:#ffe500;background-color:#e56600;\">堆积计数充填装置</span><span style=\"color:#000000;\">工作时，计量托与上下推头协同动作，完成取量及集合包装的工作。开始时，托体作间歇运动，每移动一格，从料仓中落下一包至托体中，但料仓</span><span style=\"color:#000000;\">的启闭时间随着托体的移动均有一相应的滞差，故托体移动</span><span style=\"color:#000000;\">4</span><span style=\"color:#000000;\">次后才能完成一次集合计数充填工作。该机构主要用于几种不同品种的组合计数充填包装，每种各取</span><span style=\"color:#000000;\">一定数量（或等额，或不等额）包装成一个集合包装。它还可用于小包的形状样式及大小有差异的产品的计数充填包装。</span></p>\n<p style=\"text-align:center;\"><span style=\"color:#ffe500;\"><img src=\"/edit/attached/20110706/20110706210946_11544.jpg\" width=\"400\" height=\"200\" alt=\"\" /><br />\n</span></p>\n<p></p>\n<hr />\n</p:colorscheme><p style=\"text-align:center;\"><br />\n<embed src=\"http://bzjx.nit.net.cn/uploads/movie/3-12.swf\" type=\"application/x-shockwave-flash\" width=\"550\" height=\"400\" quality=\"high\" /></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"></p:colorscheme></p>\n<hr />\n<br />\n<p></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><b> </b></p:colorscheme></p:colorscheme></p>\n<p><b><span style=\"color:#e53333;\">2.</span><span style=\"color:#e53333;\">多件计数装置</span></b></p>\n<p><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<p style=\"color:#e53333;\"><span style=\"color:#000000;\">香烟装箱的多件计数充填装置</span><span style=\"color:#000000;\">以10条为一组的条烟分成五路进入</span><span style=\"color:#000000;\">计量工位，当条烟接触到安装在计量室一端的五个触点开关3</span><span style=\"color:#000000;\">时，触发计量信号，表明计量室内已装满10条，接着启动上压</span><span style=\"color:#000000;\">板2和下托板4，使其下移一条烟的厚度距离，上压板退回上</span><span style=\"color:#000000;\">位，腾出空位供随后的条烟进入计量室。如此重复五次后，再</span><span style=\"color:#000000;\">由底部的触点开关发出指令信号，控制水平推板5向前推移，完</span><span style=\"color:#000000;\">成五十条香烟为一组的装箱包装。</span></p>\n<p></p>\n<hr />\n<p></p>\n<p style=\"color:#ffe500;\"><span style=\"font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:arial;color:#3333CC;\"><b><span style=\"color:#e53333;\">条烟多件计数充填装置</span></b></span></p>\n<p><span style=\"font-family:宋体;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></span></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<p style=\"text-align:center;\"><span class=\"Apple-style-span\" style=\"color:#ffe500;\"><u><img src=\"/edit/attached/20110706/20110706211805_23363.jpg\" width=\"350\" height=\"150\" alt=\"\" /></u></span></p>\n<p style=\"text-align:center;\"><span class=\"Apple-style-span\" style=\"color:#e53333;\"><span lang=\"EN-US\" style=\"color:#3333cc;\"><span style=\"color:#000000;\">1-</span></span><span style=\"font-family:宋体;color:#3333cc;\"><span style=\"color:#000000;\">条烟；</span></span><span lang=\"EN-US\" style=\"color:#3333cc;\"><span style=\"color:#000000;\">2-</span></span><span style=\"font-family:宋体;color:#3333cc;\"><span style=\"color:#000000;\">上压板；</span></span><span lang=\"EN-US\" style=\"color:#3333cc;\"><span style=\"color:#000000;\">3-</span></span><span style=\"font-family:宋体;color:#3333cc;\"><span style=\"color:#000000;\">触点开关；</span></span><span lang=\"EN-US\" style=\"color:#3333cc;\"><span style=\"color:#000000;\">4-</span></span><span style=\"font-family:宋体;color:#3333cc;\"><span style=\"color:#000000;\">下托板；</span></span><span lang=\"EN-US\" style=\"color:#3333cc;\"><span style=\"color:#000000;\">5-</span></span><span style=\"font-family:宋体;color:#3333cc;\"><span style=\"color:#000000;\">水平推板；</span></span><span lang=\"EN-US\" style=\"color:#3333cc;\"><span style=\"color:#000000;\">6-</span></span><span style=\"font-family:宋体;color:#3333cc;\"><span style=\"color:#000000;\">输送带</span></span></span></p>\n<p style=\"color:#e53333;font-weight:bold;\"></p>\n<hr />\n<b> <br />\n</b><p></p>\n<p><span style=\"font-family:宋体;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></span></p>\n<p style=\"color:#3333cc;\"><span style=\"color:#ffe500;\"><span style=\"background-color:#e56600;\">容积计数充填装置</span></span><span style=\"color:#000000;\">，</span><span style=\"color:#000000;\">物料自料斗1下落到计量箱内，形成有规则的排列。计量箱充满时，即达到预定的计量数，料斗与计量箱之间的闸门2</span><span style=\"color:#000000;\">关闭，同时计量箱底门打开，物品充填到包装盒内，完成一次充填包装。</span></p>\n<p><span class=\"Apple-style-span\" style=\"color:#ffe500;\"><img src=\"/edit/attached/20110706/20110706212026_49422.jpg\" width=\"300\" height=\"150\" align=\"left\" alt=\"\" /></span><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"color:#ffe500;\"><span style=\"color:#ffe500;\"><b><span style=\"color:#000000;\">容积计数充填机构示意图</span></b></span><span style=\"color:#ffe500;\"> </span></div>\n<span style=\"color:#ffe500;\"> </span><div style=\"color:#ffe500;\"><span style=\"color:#000000;\">1-料斗； 2-闸门； 3-计量箱</span><span style=\"color:#ffe500;\"> </span></div>\n<p style=\"color:#ffe500;\"><span style=\"font-family:宋体;mso-ascii-font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:宋体;font-size:20pt;color:#3333CC;\"><span style=\"mso-spacerun:yes;\">&nbsp;</span></span><span style=\"font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;font-size:20pt;color:#3333CC;\"> </span></p>\n<p style=\"color:#ffe500;\"><span style=\"font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;font-size:20pt;color:#3333CC;\"><br />\n</span></p>\n<p style=\"color:#ffe500;\"><span style=\"font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;font-size:20pt;color:#3333CC;\"><br />\n</span></p>\n<p style=\"color:#ffe500;\"></p>\n<hr />\n<p style=\"color:#3333cc;font-size:20pt;\"><b><span style=\"font-size:12px;color:#e53333;\">3.</span><span style=\"font-size:12px;color:#e53333;\">转盘计数装置</span></b></p>\n<p style=\"text-align:center;color:#3333cc;font-size:20pt;\"><b><span style=\"font-size:12px;color:#e53333;\"><img src=\"/edit/attached/20110706/20110706212231_89532.jpg\" width=\"450\" height=\"200\" alt=\"\" /><br />\n</span></b></p>\n<p style=\"font-size:20pt;\"><span style=\"font-size:12px;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></span></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"font-weight:bold;color:#e53333;text-align:center;\"><span style=\"color:#ffe500;\">（a） 结构示意图</span><span style=\"color:#ffe500;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"color:#ffe500;\">（b） 传动系统图</span><span style=\"color:#ffe500;\"> </span></div>\n<span style=\"font-weight:bold;color:#ffe500;\"> </span><div style=\"text-align:center;font-weight:bold;color:#e53333;\"><span style=\"color:#000000;\">转盘计数充填装置</span><span style=\"color:#000000;\"> </span></div>\n<span style=\"color:#000000;\"> </span><span style=\"font-weight:bold;color:#000000;\"> </span><div style=\"text-align:center;\"><div style=\"text-align:center;\">1-输瓶带；2-药瓶；3-下料斗；4-托盘；5-带孔转盘；6-蜗杆；</div>\n<div style=\"text-align:center;\">7-大齿轮；8-手柄；9-槽轮；10-曲柄轮；11-小齿轮；12-蜗轮；13-摆动杆；14-闸门凸轮；15-料盘蜗轮；16-电机；17-定瓶器</div>\n</div>\n<p></p>\n<hr />\n<b style=\"color:#e53333;\"> </b><div style=\"text-align:center;color:#e53333;\"><b><span style=\"color:#000000;\"><embed src=\"http://bzjx.nit.net.cn/uploads/movie/3-13.swf\" type=\"application/x-shockwave-flash\" width=\"550\" height=\"400\" quality=\"high\" /></span></b></div>\n<p></p>\n<p style=\"color:#e53333;\"></p>\n<hr />\n<br />\n<p></p>\n<p style=\"color:#e53333;\"><span style=\"color:#ffe500;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></span></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<p><span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp; &nbsp; 转盘计数充填装置，常用于药片、药丸等规则物料的计数定量。带孔转盘倾斜</span><span style=\"color:#000000;\">安装，盘上开有几组(3～4组)小孔，每组孔数由每瓶的装量数决定。在转盘</span><span style=\"color:#000000;\">下面装有一个固定托盘4，托盘有一个扇形缺口，缺口面积只能容纳转盘的</span><span style=\"color:#000000;\">一组小孔。缺口下边紧连下料斗3，直抵药瓶口。转盘上小孔形状应与物料</span><span style=\"color:#000000;\">形状相同且尺寸略大，转盘厚度略大于物料厚度，以确保每个小孔容纳一颗</span><span style=\"color:#000000;\">物料。当转盘旋转时，转速要与输瓶带上瓶子的移动频率匹配，不能太快</span><span style=\"color:#000000;\">（约0.5～2r/min）；若转盘转速太快将产生离心力，不能保证药粒在盘上</span><span style=\"color:#000000;\">靠自重充填。当每组小孔随转盘旋至最低位置时，药粒将埋住小孔。当小孔</span><span style=\"color:#000000;\">随转盘向高处旋转时，小孔上面叠堆的药粒靠自重沿斜面滚落至转盘的最低</span><span style=\"color:#000000;\">处。为保证各组小孔均落满药粒和使多余的药粒自动滚落，常需要使转盘非</span><span style=\"color:#000000;\">匀速旋转。为此，可将手柄8拨向右侧位置，使槽轮9与曲柄轮10配合，同时</span><span style=\"color:#000000;\">将大齿轮7与小齿轮11脱开。电机驱动曲柄轮匀速旋转，槽轮9间歇变速旋</span><span style=\"color:#000000;\">转，引起转盘抖动旋转，以利于准确计数。为使输瓶带上的容器瓶口和下料</span><span style=\"color:#000000;\">斗下口准确对位，闸门凸轮14动作，通过软线传输，驱动定瓶器17动作，使</span><span style=\"color:#000000;\">将到位的药瓶定位，以防药粒散落瓶外。当需要改变容器的装药粒数时，更</span><span style=\"color:#000000;\">改带孔转盘即可。</span></p>\n<span style=\"color:#000000;\"> </span><p><span style=\"color:#ffe500;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><span style=\"color:#000000;\"> </span></p:colorscheme></span></p>\n<span style=\"color:#000000;\"> </span><div v:shape=\"_x0000_s1026\" class=\"O\"><span style=\"color:#000000;\"> </span><p><span style=\"color:#000000;\">履带式计数充填机利用履带上的计数板对产品进行计数，并将其充填到包装容</span><span style=\"color:#000000;\">器，履带由若干组匀布凹穴和光面的条板</span><span style=\"color:#000000;\">2</span><span style=\"color:#000000;\">组成。片剂经料仓</span><span style=\"color:#000000;\">4</span><span style=\"color:#000000;\">底部的筛分</span><span style=\"color:#000000;\">器</span><span style=\"color:#000000;\">5</span><span style=\"color:#000000;\">筛分后进入料仓</span><span style=\"color:#000000;\">3</span><span style=\"color:#000000;\">，依靠自重和振动器</span><span style=\"color:#000000;\">7</span><span style=\"color:#000000;\">的作用，落入履带条板的板穴</span><span style=\"color:#000000;\">中，并由拔料毛刷</span><span style=\"color:#000000;\">6</span><span style=\"color:#000000;\">将板上的多余片剂拔去。当片剂移送到卸料工位，转鼓</span><span style=\"color:#000000;\">的径向推头</span><span style=\"color:#000000;\">11</span><span style=\"color:#000000;\">将片剂成排推下，经卸料槽</span><span style=\"color:#000000;\">10</span><span style=\"color:#000000;\">装入包装容器</span><span style=\"color:#000000;\">9</span><span style=\"color:#000000;\">。在履带连续运</span><span style=\"color:#000000;\">行过程中，通过探测器</span><span style=\"color:#000000;\">8</span><span style=\"color:#000000;\">检查履带条板的凹穴是否有缺料现象，如有则自动</span><span style=\"color:#000000;\">停机。该装置适用于片状和球形等规则物料的计数</span><span style=\"color:#ffe500;\"> </span></p>\n<p></p>\n<hr />\n<p><b>履带式计数充填装置</b></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n<p></p>\n<div style=\"text-align:center;\"><span class=\"Apple-style-span\" style=\"color:#ffe500;\"><img src=\"/edit/attached/20110706/20110706212420_34185.jpg\" width=\"400\" height=\"300\" alt=\"\" /></span></div>\n<span class=\"Apple-style-span\" style=\"color:#ffe500;\"><table style=\"width:100%;\" cellpadding=\"2\" cellspacing=\"0\" border=\"1\" bordercolor=\"#000000\"></table>\n</span><div style=\"text-align:center;\"><span style=\"color:#000000;\">1-清屑毛刷；2-履带条板；3、4-料仓；</span></div>\n<p style=\"text-align:center;margin-top:5px;margin-right:0px;margin-bottom:5px;margin-left:0px;\"><span style=\"color:#000000;\">5-筛分器；6-拨料毛刷；7-振动器；8-探测器 9-包装容器；10-卸料斗槽；11-径向推头</span></p>\n<p></p>\n<p></p>\n<hr />\n<p>&nbsp;</p>\n<p style=\"text-align:center;\"><span style=\"color:#ffe500;\"><img src=\"/edit/attached/20110706/20110706212533_55639.jpg\" width=\"500\" height=\"200\" alt=\"\" /><br />\n</span></p>\n<p style=\"text-align:center;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"color:#ffe500;text-align:center;\"><span style=\"color:#000000;\"><b>长度计数充填机构示意图</b></span></div>\n<span style=\"color:#000000;\"> </span><p style=\"text-align:center;\"><span style=\"color:#000000;\">1-输送带 2-被包装产品 3-横向推板&nbsp; 4-触点开关 5-挡板</span></p>\n<p></p>\n<hr />\n<div style=\"text-align:center;\"><p:colorscheme colors=\"#ffffff,#000000,#b2b2b2,#000000,#b2b2b2,#808080,#969696,#4d4d4d\"><p style=\"color:#ffe500;\"><b><span style=\"color:#e56600;font-size:14px;\">履带式计数装置</span></b></p>\n<p style=\"text-align:center;color:#ffe500;\"><b><span style=\"color:#e56600;font-size:14px;\"><embed src=\"http://bzjx.nit.net.cn/uploads/movie/3-14.swf\" type=\"application/x-shockwave-flash\" width=\"550\" height=\"400\" quality=\"high\" /><br />\n</span></b></p>\n<p style=\"color:#ffe500;\"></p>\n<hr />\n</p:colorscheme></div>\n<div><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><div v:shape=\"_x0000_s1026\" class=\"O\"><div style=\"color:#ffe500;text-align:left;\"><span style=\"color:#000000;\">&nbsp; &nbsp; &nbsp;计数时，排列有序的产品经输送机构送到计量机构中，进行产品的前端触到计量腔的挡板时，压迫挡板上的电触头或机械触头，发出信号，指令推进器迅速动作，将一定数量的产品推到包装台上进行充填包装。</span></div>\n<span style=\"color:#000000;\"> </span><div style=\"color:#ffe500;text-align:left;\"><span style=\"color:#000000;\">&nbsp; 常用在饼干、云片糕包装或茶叶等小盒包装以后进行二次集合包装</span></div>\n<div style=\"color:#ffe500;text-align:left;\"><hr />\n<div style=\"text-align:center;\"><embed src=\"http://bzjx.nit.net.cn/uploads/movie/3-15.swf\" type=\"application/x-shockwave-flash\" width=\"550\" height=\"400\" quality=\"high\" /><br />\n</div>\n</div>\n<div style=\"color:#ffe500;text-align:left;\"><hr />\n</div>\n<div style=\"text-align:left;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"><p style=\"color:#ffe500;\"><b><span style=\"font-size:14px;color:#e53333;\">二、容积式计量装置</span></b></p>\n<p><span style=\"font-size:14px;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></span></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"color:#e53333;\"><span style=\"font-weight:bold;font-family:Tahoma;font-size:156%;\"><span style=\"mso-special-format:bullet;color:#5FB6F1;mso-color-index:6;position:absolute;left:-3.37%;font-family:Wingdings;\">v</span></span><span style=\"color:#000000;\">对于密度稳定、颗粒均匀、粘附性小、不易吸潮结块、</span><span style=\"color:#000000;\">流动性好的松散粉粒物料，如味精、砂糖、洗衣粉、</span><span style=\"color:#000000;\">食盐等，采用容积式计量方法比较适合。</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div style=\"color:#e53333;\"><span style=\"color:#000000;\">v</span><span style=\"color:#000000;\">常用的容积式计量装置有</span><span style=\"color:#000000;\">量杯式、转鼓式、螺杆式、</span><span style=\"color:#000000;\">活塞式</span><span style=\"color:#000000;\">等不同的类型。</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div style=\"color:#e53333;\"><span style=\"color:#000000;\">v</span><span style=\"color:#000000;\">容积式计量装置具有结构简单、计量速度较快、造价</span><span style=\"color:#000000;\">低的特点，但计量精度较低。</span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><p style=\"color:#e53333;\"><span style=\"color:#000000;\">v</span><span style=\"color:#000000;\">按计量容腔的容积是否可调，可将容积式计量装置分</span><span style=\"color:#000000;\">为固定容积计量法和可调容积计量法。固定容积计量</span><span style=\"color:#000000;\">法所用计量容腔容积为一个定值，对指定的物料只能</span><span style=\"color:#000000;\">计量一个量值。可调容积计量法所用计量装置，每套</span><span style=\"color:#000000;\">计量杯容腔为组合式结构，其容积和计量范围可调节。</span></p>\n<p style=\"color:#e53333;\"></p>\n<hr />\n<div v:shape=\"_x0000_s1026\" class=\"O\"><p style=\"color:#ffe500;\"><span lang=\"EN-US\" style=\"mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;color:red;mso-fareast-language:ZH-CN;\"><b>1.</b></span><span style=\"font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;color:red;\"><b>量杯式计量装置</b></span></p>\n<p><span style=\"font-family:宋体;color:red;\"><b><img src=\"/edit/attached/20110706/20110706212934_33988.jpg\" width=\"300\" height=\"400\" align=\"left\" alt=\"\" style=\"text-align:center;\" /></b></span></p>\n<p style=\"color:#ffe500;\"><span style=\"font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;color:red;\"><b><br />\n</b></span></p>\n<p style=\"text-align:center;color:#ffe500;\"><span style=\"font-family:宋体;mso-fareast-font-family:宋体;mso-hansi-font-family:Arial;color:red;\"><b><img src=\"/edit/attached/20110706/20110706212958_99050.jpg\" width=\"300\" height=\"400\" alt=\"\" /></b></span></p>\n<p><span style=\"font-family:宋体;\"><p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> </p:colorscheme></span></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\">\n\n<div style=\"font-weight:bold;color:red;text-align:center;\"><span style=\"color:#000000;\">转盘式可调容积量杯计量装置</span><span style=\"color:#ffe500;\"> </span></div>\n<span style=\"font-weight:bold;color:#ffe500;\"> </span><div style=\"text-align:center;color:red;\"><b><span style=\"color:#000000;\">1-</span><span style=\"color:#000000;\">料斗；</span><span style=\"color:#000000;\">2-</span><span style=\"color:#000000;\">刮板；</span><span style=\"color:#000000;\">3-</span><span style=\"color:#000000;\">转盘；</span><span style=\"color:#000000;\">4-</span><span style=\"color:#000000;\">上量筒；</span><span style=\"color:#000000;\">5-</span><span style=\"color:#000000;\">下量筒；</span><span style=\"color:#000000;\">6-</span><span style=\"color:#000000;\">托盘；</span></b></div>\n<b><span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><span style=\"color:#ffe500;\"><span style=\"color:#000000;\"> </span></span></b><p style=\"text-align:center;color:red;\"><b><span style=\"color:#000000;\">7-</span><span style=\"color:#000000;\">底盖；</span><span style=\"color:#000000;\">8-</span><span style=\"color:#000000;\">漏斗；</span><span style=\"color:#000000;\">9-</span><span style=\"color:#000000;\">容器；</span><span style=\"color:#000000;\">10-</span><span style=\"color:#000000;\">转轴；</span><span style=\"color:#000000;\">11-</span><span style=\"color:#000000;\">容量调节机构；</span><span style=\"color:#000000;\">12-</span><span style=\"color:#000000;\">导轨</span></b></p>\n<span style=\"color:#000000;\"><b> </b></span><p style=\"color:red;\"></p>\n<span style=\"color:#000000;\"><b> </b></span><hr />\n<div v:shape=\"_x0000_s1026\" class=\"O\"><div>&nbsp; &nbsp; &nbsp; &nbsp;当所计量的物料或计量的量值发生改变时，必须更换一套相应容积的计量杯，相应的计量装置必须配备多种不同容积的量杯以供更换，给包装机的使用、管理和维护带来许多不便。采用容积可调式量杯计量装置，能在一定程度上克服上述不足。图3-9所示为容积可调式量杯计量装置的结构简图。量杯由上、下两个量筒套装组成，容积大小的改变通过调整两个量筒的相对位置实现。<span style=\"font-weight:bold;font-family:Tahoma;font-size:111%;\"><span style=\"mso-special-format:bullet;color:#5FB6F1;mso-color-index:6;position:absolute;left:-3.37%;font-family:Wingdings;\">v</span></span></div>\n<span style=\"color:#000000;\"> </span><span style=\"color:#000000;\"> </span><div><span style=\"color:#000000;\">v&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 量杯上、下量筒间距的调节，可采用手动或自动调节方式进行。手动调节根据包装生产过程中检测出的包装计量值的变动或物料密度变化，由人工操纵调节机构调节计量值。转动调节手轮，螺杆驱使螺母移动，通过杠杆机构使固定安装下量筒的转盘升降，实现计量容积的调节</span></div>\n</div>\n<p></p>\n</div>\n<p></p>\n<p></p>\n<div style=\"color:#ffe500;\"></div>\n</div>\n <p></p>\n<div></div>\n<div style=\"color:#e53333;font-weight:bold;\"></div>\n</div>\n</p:colorscheme><p></p>\n</div>\n<div style=\"color:#ffe500;\"></div>\n</div>\n</p:colorscheme></div>\n</div>\n<p></p>\n</div>\n<p></p>\n<div style=\"mso-line-spacing:\" 100200\";mso-margin-left-alt:216;mso-char-wrap:1;mso-kinsoku-overflow:1;\"=\"\"></div>\n<div style=\"mso-line-spacing:\" 100500\";mso-margin-left-alt:216;mso-char-wrap:1;mso-kinsoku-overflow:1;\"=\"\"></div>\n</div>\n<p></p>\n<div style=\"font-weight:bold;\"></div>\n</div>\n<p></p>\n<div style=\"font-weight:bold;\"></div>\n<div style=\"font-weight:bold;color:#e53333;\"></div>\n</div>\n<p></p>\n<p></p>\n<div style=\"color:#ffe500;\"></div>\n</div>\n<p></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\"></div>\n<p></p>\n<div style=\"color:#e53333;font-weight:bold;\"></div>\n</div>\n<p></p>\n<p></p>\n<div style=\"color:#e53333;font-weight:bold;\"></div>\n</div>\n<p></p>\n<p></p>\n<p:colorscheme colors=\"#ffffff,#000000,#969696,#173d89,#9181e1,#4cd2af,#5fb6f1,#94b1ec\"> <p></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\" style=\"mso-margin-left-alt:216;mso-char-wrap:1;mso-kinsoku-overflow:1;\"></div>\n</p:colorscheme></div>\n<div style=\"mso-line-spacing:\" 100=\"\" 500\";mso-text-indent-alt:144;mso-char-wrap:1;mso-kinsoku-overflow:1;\"=\"\"></div>\n</div>\n <p></p>\n<div style=\"mso-line-spacing:\" 100500\";mso-char-wrap:1;mso-kinsoku-overflow:1;\"=\"\"></div>\n</div>\n<p></p>\n<div v:shape=\"_x0000_s1026\" class=\"O\"></div>\n<p></p>\n<p></p>\n<div style=\"font-weight:bold;\"></div>\n</div>\n</p:colorscheme><p></p>',0,NULL,'999999',NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
