INSERT INTO `news` VALUES (626,243,'',0,NULL,NULL,NULL,'','','','','2012-12-05 15:48:38','<table cellspacing=\"0\" cellpadding=\"0\" width=\"666\" align=\"center\" border=\"0\" style=\"width: 666px; height: 1287px\">\n    <tbody>\n        <tr>\n            <td class=\"unnamed1\" colspan=\"2\" height=\"56\"><br />\n            <img height=\"34\" alt=\"\" width=\"391\" src=\"images/html/34r.jpg\" /></td>\n        </tr>\n        <tr>\n            <td class=\"unnamed1\" colspan=\"2\" height=\"20\"><img height=\"29\" alt=\"\" width=\"121\" src=\"images/html/35r.jpg\" /></td>\n        </tr>\n        <tr>\n            <td class=\"unnamed1\" valign=\"top\" colspan=\"2\" height=\"123\">\n            <div align=\"left\">公司2006年被国家发改委列入了全国&ldquo;千家节能行动&rdquo;企业之一。为扎实开展&ldquo;千家企业节能行动&rdquo;工作，作为2500t/d 新型干法水泥的配套项目，公司利用新型干法水泥回转窑的窑头、窑尾生产过程中的废气余热建设了宁波市第一个纯低温余热发电站，在回收熟料生产过程中产生的大量余热的同时，还减少了水泥厂对环境的热效应。公司建设的4.5MW汽轮发电机组设计发电能力为3900kw/h，目前平均发电量为4150kw/h，按年运转7200小时计算总年发电量为2988万kwh，减去系统自用电，企业每年向电网少购电量2808万kwh，相当于节约标准煤10108吨/年，同时可减少温室气体的排放，降低温室效应，改善对小环境小气候的影响。</div>\n            </td>\n        </tr>\n        <tr>\n            <td class=\"unnamed1\" valign=\"top\" colspan=\"2\" height=\"127\">\n            <div align=\"center\"><br />\n            <img height=\"383\" alt=\"\" width=\"574\" src=\"images/html/36r.jpg\" /></div>\n            </td>\n        </tr>\n        <tr>\n            <td class=\"unnamed1\" colspan=\"2\" height=\"2\"><br />\n            ●　通过变频等综合节能技术改造，企业年节电量达到945万kwh；<br />\n            ●　配有四套循环水处理系统，循环率达到96%以上；<br />\n            ●　配有一套润滑油过滤系统，对废油、脏油进行循环利用，年节约润滑油100多吨；</td>\n        </tr>\n        <tr>\n            <td class=\"unnamed1\" valign=\"top\" colspan=\"2\" height=\"76\">\n            <div align=\"center\"><br />\n            <img height=\"587\" alt=\"\" width=\"574\" src=\"images/html/37r.jpg\" /><br />\n            &nbsp;</div>\n            </td>\n        </tr>\n    </tbody>\n</table>',0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO `news` VALUES (628,208,'科环公司开展“我与舜江同发展”征文比赛',0,NULL,NULL,NULL,'','','科环','','2012-12-06 06:29:08','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 近期，为丰富企业文化内容，营造良好的文化氛围，科环公司党、工、团联合举办\'我与舜江同发展\'主题征文比赛，评选出一、二、三等奖共14名。<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 此次活动的开展为员工提供一个展现自我的平台，有效激发广大员工的写作热情和潜能，增强了员工热爱企业、奉献企业的信心和决心，展现了员工们自强不息、勇于拼搏的精神面貌。<br />\n<br />\n<img alt=\"\" src=\"/uploads/201212/image/1212060630398595206wrncywfpopf.jpg\" /></p>',0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO `news` VALUES (630,208,'关于推荐俞枢根同志为“十一五”时期全国节能先进个人的公示',0,NULL,NULL,NULL,'','','','','2012-12-06 06:35:33','<p>&nbsp;根据人力资源和社会保障部、国家发展改革委、环保部、财政部《关于做好推荐<font size=\"3\">\'十一五\'时期全国节能减排先进集体和先进个人工作的通知》（人社部函〔2012〕30号），经推荐评审，拟推荐俞枢根同志为全国节能先进个人，现予以公示，征求广大干部、群众的意见。现就有关事项说明如下：</font></p>\n<div><span>&nbsp;&nbsp;&nbsp; 1.反映问题的方式：在公示期限内，单位和个人可通过来信、来电等形式反映公示对象存在的问题。以单位名义反映问题的应加盖公章，以个人名义反映问题的提倡署报本人真实姓名。</span></div>\n<div><span>&nbsp;&nbsp;&nbsp; 2.要求：反映问题要坚持实事求是的原则，反对借机诽谤诬告。</span></div>\n<div><span>&nbsp;&nbsp;&nbsp; 3.公示时间：从9月20日起到9月26日止，共6天。</span></div>\n<div><span>&nbsp;&nbsp;&nbsp; 4.公示联系部门和受理电话：</span></div>\n<div><span>&nbsp;&nbsp;&nbsp;宁波科环新型建材股份有限公司综合部，联系人：何峰，电话：0574-62633726，地址：余姚市城区富巷北路558号，邮编：315400。</span></div>',0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
INSERT INTO `news` VALUES (632,208,'科环公司积极开展“合理化建议”和“安全找隐患”征集活动',0,NULL,NULL,NULL,'','','科环','','2012-12-06 06:37:07','<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 为充分发挥广大职工的主人翁精神，有效调动职工参与企业安全生产、民主管理的积极性，科环公司结合企业实际，开展\'合理化建议\'和\'安全找隐患\'征集活动。<br />\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 广大干部职工踊跃参与，重点围绕深化企业机制完善、经营管理思路和方法创新、安全生产和技术改进、企业文化建设等方面献计献策，共征集到合理化建议和安全隐患500多份，最终评出一、二、三等奖共四名。科环公司始终把开展\'合理化建议\'和\'安全找隐患\'活动作为完善企业管理机制，推进企业持续健康发展的重要举措，同时把有效的建议及时应用到实际生产工作中去，使征集活动更加深入人心和扎实有效。</p>',0,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL,NULL);
